package javaloops;

public class Starprint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("print * using java code");
		
		int row=4;
		
		for(int i=1; i<=row ; i++) {
			
			System.out.print("*");
			
			for(int j=1 ;j<2; j++) {
				System.out.println("*");
				
			}
		}
		 
	}

}
